def slugify( name )
  name = name.downcase
  name = name.gsub( ' ', '_' )
  name = name.gsub( '/', '-' )
  name
end



attributes.each do |category, h|
   h.each do |name, data|
      puts "==> decoding #{category} / #{name}..."

      img = Image.parse_base64( data )
      img = img.mirror  ## note: flip to right-looking

      name_slug     = slugify( name )
      category_slug = slugify( category )

      img.save( "./tmp/#{category_slug}/#{name_slug}.png")
      img.zoom(8).save( "./tmp/@8x/#{category_slug}--I--#{name_slug}@8x.png")
    end
end


