def slugify( name )
   name = name.downcase.gsub( /[^a-zA-Z0-9 _-]/, '' )
   name = name.gsub( /[ ]+/, '_' )
   name
end

attributes.each do |category,h|
  puts "  #{h.size} in #{category}"

  h.each do |name, blob|
    img = Image.parse_base64( blob )
    slug = slugify( name )
    img.save( "./tmp/attributes/#{category}/#{slug}.png" )
    img.zoom(8).save( "./tmp/attributes@8x/#{slug}@8x.png" )
  end
end


puts "bye"

